use alertaciclista;
select concat('Suggestion.create!( title:"', incident_title, '", author:"', concat(ip.person_first, ' ', ip.person_last), '",email:"',person_email, '",comment:"', incident_description, '",latitude: ',latitude, ', longitude:',longitude, ',address:"',location_name, '",created_at:"',incident_date, '",updated_at:"',incident_date, '",visible:true, category:4, image1_id: "http://alertaciclista.com/media/uploads/', media_link, '" )') from incident i inner join incident_person ip on ip.incident_id=i.id inner join location l on l.id = i.location_id inner join media m on m.incident_id = i.id where person_email <> '';
